mapper.select:
		select t.id,t.coupon_id,t.usecondition,t.validity,s.name kechengName,t.usage_scenario,t.course_id,t.coupon_balance,t.coupon_count,t.count_name,t.create_time,t.coupon_surplus,t.delete_flag from t_coupon t left join course s
        on t.course_id=s.course_id

		  		   and t.id = #{id} 
		  		   and t.coupon_id = #{couponId} 
		  		   and t.coupon_balance = #{couponBalance} 
		  		   and t.coupon_count = #{couponCount} 
		  		   and t.count_name = #{countName} 
		  		   and date_format(t.create_time,'%Y-%m') = #{createTime} 
		  		   and t.coupon_surplus = #{couponSurplus} 
		  		   and t.delete_flag = #{deleteFlag} 




                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from t_coupon

  		   and id = #{id} 
  		   and coupon_id = #{couponId} 
  		   and coupon_balance = #{couponBalance} 

  		   and coupon_count = #{couponCount} 
  		   and count_name = #{countName} 
  		  and date_format(create_time,'%Y-%m') = #{createTime} 
  		   and coupon_surplus = #{couponSurplus} 

  		   and delete_flag = #{deleteFlag}
