mapper.select:
		select t.id,t.validity,t.coupon_id,t.reedeem_code,t.reedeem_name,t.course_id,t.reedeem_count,t.reedeem_surplus,t.reedeem_balance,t.create_time,t.reedeem_type,t.create_id,t.create_name,t.delete_flag,t.if_stop,c.name courseName from t_reedeem t left join course c
        on t.course_id=c.course_id

		  		   and t.id = #{id} 
		  		   and t.reedeem_code = #{reedeemCode} 
		  		    and date_format(t.create_time,'%Y-%m') = #{createTime} 
		  			 and t.reedeem_type = #{reedeemType} 
		  		   and t.delete_flag = #{deleteFlag} 
		  		     and t.if_stop = #{ifStop} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from t_reedeem

  		   and id = #{id} 
  		   and reedeem_code = #{reedeemCode} 
  		     and date_format(create_time,'%Y-%m') = #{createTime} 
  		   and reedeem_type = #{reedeemType} 
  		   and if_stop = #{ifStop} 
  		   and delete_flag = #{deleteFlag}

mapper.update:
update t_reedeem
`reedeem_code` = #{reedeemCode}, `if_stop` = #{ifStop}, `create_time` = #{createTime}, `create_id` = #{createId}, `create_name` = #{createName}, `delete_flag` = #{deleteFlag}, reedeem_type = #{reedeemType} , validity = #{validity} ,
`reedeem_name` = #{reedeemName}, `course_id` = #{courseId}, `reedeem_count` = #{reedeemCount}, `reedeem_surplus` = #{reedeemSurplus}, `reedeem_balance` = #{reedeemBalance}
where id = #{id}
