mapper.select:
		select t.id,t.validity,count(1) reedeem_count ,count(if(t.if_used>0 , t.if_used, null)) reedeemSurplus,   t.coupon_id,t.reedeem_code,t.reedeem_name,t.course_id,t.reedeem_balance,t.create_time,t.reedeem_type,t.create_id,t.create_name,t.delete_flag,t.if_stop,c.name courseName from t_reedeem t left join course c
        on t.course_id=c.course_id


		  		   and t.reedeem_code = #{reedeemCode} 
		  		    and date_format(t.create_time,'%Y-%m') = #{createTime} 
		  			 and t.reedeem_type = #{reedeemType} 
					 and t.if_used = #{ifUsed} 

		  		group by t.reedeem_code


                order by t.create_time ${order}


                order by id desc




			limit #{offset}, #{limit}

select count(distinct reedeem_code) from t_reedeem 

  		   and id = #{id} 
  		   and reedeem_code = #{reedeemCode} 
  		     and date_format(create_time,'%Y-%m') = #{createTime} 
  		   and reedeem_type = #{reedeemType} 
  		   and if_stop = #{ifStop} 
  		   and delete_flag = #{deleteFlag} 
  		  and if_used = #{ifUsed}

mapper.update:
update t_reedeem
`reedeem_code` = #{reedeemCode}, `if_stop` = #{ifStop}, `create_time` = #{createTime}, `create_id` = #{createId}, `create_name` = #{createName}, `delete_flag` = #{deleteFlag}, reedeem_type = #{reedeemType} , validity = #{validity} ,
`reedeem_name` = #{reedeemName}, `course_id` = #{courseId},
`reedeem_balance` = #{reedeemBalance}
where id = #{id}
