mapper.select:
		select `id`,`reedeem_code`,`create_time`,reedeem_type,`create_id`,create_name,`delete_flag`,if_stop from t_reedeem

		  		   and id = #{id} 
		  		   and reedeem_code = #{reedeemCode} 
		  		    and date_format(create_time,'%Y-%m') = #{createTime} 
		  			 and reedeem_type = #{reedeemType} 
		  		   and delete_flag = #{deleteFlag} 
		  		     and if_stop = #{ifStop} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from t_reedeem

  		   and id = #{id} 
  		   and reedeem_code = #{reedeemCode} 
  		     and date_format(create_time,'%Y-%m') = #{createTime} 
  		   and reedeem_type = #{reedeemType} 
  		   and if_stop = #{ifStop} 
  		   and delete_flag = #{deleteFlag}
