mapper.select:
		select `id`,`heard_url`,phone_system,`username`,registration_source,`sign_name`,`sex`,`birthday`,`login_time`,`used_time`,`balance`,`nickname`,`user_id`,`open_id`,`password`,`phone`,`name`,`register_time`,`update_time`,`delete_flag` from t_user

		  		   and id = #{id} 


		  		   and sign_name = #{signName} 
		  		   and sex = #{sex} 
		  		   and birthday = #{birthday} 
		  		   and login_time = #{loginTime} 
		  		   and used_time = #{usedTime} 
		  		   and balance = #{balance} 
		  		   and nickname = #{nickname} 



		  		   and phone = #{nameORphone} or username = #{nameORphone}

		  		   and register_time = #{registerTime} 
		  		   and update_time = #{updateTime} 




                order by login_time ${order}


                order by register_time ${order}


                order by id desc



			limit #{offset}, #{limit}

