mapper.select:
select
id,type,for_names,for_ids,title,url,is_disabled,
details,browse_num,add_time,date_format(add_time,'%Y-%m-%d %H:%i:%s') as addTime1,
update_time,delete_flag,user_id
from t_consult

    and delete_flag = #{deleteFlag}
    and type = #{type}

ORDER BY add_time DESC

    limit #{offset}, #{limit}

select count(*) from t_consult

    and type = #{type}
    and for_names = #{forNames}
    and for_ids = #{forIds}
    and title = #{title}
    and url = #{url}
    and is_disabled = #{isDisabled}
    and details = #{details}
    and browse_num = #{browseNum}
    and add_time = #{addTime}
    and update_time = #{updateTime}
    and delete_flag = #{deleteFlag}
