select u.name as name,d.id,d.treat_time,d.adddate,d.userid,d.treat_strength,d.treat_gears,d.treat_frequency,d.treat_waveform,d.treat_workmethod,d.treat_electrode,d.update_time,d.name    
from t_user u,treat_data d 
u.user_id = d.userid and u.id = #{id}

select u.name as name,d.id,d.treat_time,d.adddate,d.userid,d.treat_strength,d.treat_gears,d.treat_frequency,d.treat_waveform,d.treat_workmethod,d.treat_electrode,d.update_time,d.name,d.count    
from t_user u,treat_data d 

  		   and id = #{id} 
  		   and treat_time = #{treatTime} 
  		   and adddate = #{adddate} 
  		   and userid = #{userid} 
  		   and treat_strength = #{treatStrength} 
  		   and treat_gears = #{treatGears} 
  		   and treat_frequency = #{treatFrequency} 
  		   and treat_waveform = #{treatWaveform} 
  		   and treat_workmethod = #{treatWorkmethod} 
  		   and treat_electrode = #{treatElectrode} 
  		   and update_time = #{updateTime} 
  		   and name = #{name} 
  		   and count = #{count} 


 	u.user_id = d.userid  and DATE_FORMAT(adddate, '%Y%m' ) = DATE_FORMAT( CURDATE(),'%Y%m')

select u.name as name,d.id,d.treat_time,d.adddate,d.userid,d.treat_strength,d.treat_gears,d.treat_frequency,d.treat_waveform,d.treat_workmethod,d.treat_electrode,d.update_time,d.name,d.count    
