mapper.select:
		select u.name as name,d.id,d.treat_time,d.adddate,d.userid,d.treat_strength,d.treat_frequency,d.treat_waveform,d.treat_workmethod,d.treat_electrode,d.update_time,d.name    
		from t_user u,treat_data d 

		  		   and u.id = #{id} 
		  		   and treat_time = #{treatTime} 
		  		   and adddate = #{adddate} 
		  		   and userid = #{userid} 
		  		   and treat_strength = #{treatStrength} 
		  		   and treat_gears = #{treatGears} 
		  		   and treat_frequency = #{treatFrequency} 
		  		   and treat_waveform = #{treatWaveform} 
		  		   and treat_workmethod = #{treatWorkmethod} 
		  		   and treat_electrode = #{treatElectrode} 
		  		   and update_time = #{updateTime} 
		  		   and name = #{name} 
		  		   and count = #{count} 
		  		  and u.user_id = d.userid



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select u.name as name,d.id,d.treat_time,d.adddate,d.userid,d.treat_strength,d.treat_frequency,d.treat_waveform,d.treat_workmethod,d.treat_electrode,d.update_time,d.name    
from t_user u,treat_data d 
u.user_id = d.userid and u.id = #{id}




SELECT IFNULL(u.name,'') as 用户名, d.adddate as 治疗时间, d.treat_time as 治疗时长,d.treat_strength as 治疗强度,d.treat_frequency as 治疗频率,d.treat_waveform as 治疗波形,d.treat_workmethod as 治疗方式,d.treat_electrode as 治疗电极   
from t_user u
LEFT JOIN treat_data d ON u.user_id = d.userid

  		   and treat_time = #{treatTime} 
  		   and adddate = #{adddate} 
  		   and userid = #{userid} 
  		   and treat_strength = #{treatStrength} 
  		   and treat_frequency = #{treatFrequency} 
  		   and treat_waveform = #{treatWaveform} 
  		   and treat_workmethod = #{treatWorkmethod} 
  		   and treat_electrode = #{treatElectrode} 
  		 and d.id in (${ids}) 
  		and d.userid in(u.user_id)

select u.name as name,d.id,d.treat_time,d.adddate,d.userid,d.treat_strength,d.treat_frequency,d.treat_waveform,d.treat_workmethod,d.treat_electrode,d.update_time,d.name,d.count    
from t_user u,treat_data d 

  		   and id = #{id} 
  		   and treat_time = #{treatTime} 
  		   and adddate = #{adddate} 
  		   and userid = #{userid} 
  		   and treat_strength = #{treatStrength} 
  		   and treat_frequency = #{treatFrequency} 
  		   and treat_waveform = #{treatWaveform} 
  		   and treat_workmethod = #{treatWorkmethod} 
  		   and treat_electrode = #{treatElectrode} 
  		   and update_time = #{updateTime} 
  		   and name = #{name} 
  		   and count = #{count} 


 	u.user_id = d.userid  and DATE_FORMAT(adddate, '%Y%m' ) = DATE_FORMAT( CURDATE(),'%Y%m')

select u.name as name,d.id,d.treat_time,d.adddate,d.userid,d.treat_strength,d.treat_frequency,d.treat_waveform,d.treat_workmethod,d.treat_electrode,d.update_time,d.name,d.count    
from t_user u,treat_data d 

  		   and id = #{id} 
  		   and treat_time = #{treatTime} 
  		   and adddate = #{adddate} 
  		   and userid = #{userid} 
  		   and treat_strength = #{treatStrength} 
  		   and treat_frequency = #{treatFrequency} 
  		   and treat_waveform = #{treatWaveform} 
  		   and treat_workmethod = #{treatWorkmethod} 
  		   and treat_electrode = #{treatElectrode} 
  		   and update_time = #{updateTime} 
  		   and name = #{name} 
  		   and count = #{count} 


 	u.user_id = d.userid  and YEARWEEK(date_format(adddate,'%Y-%m-%d')) = YEARWEEK(now());

select u.name as name,d.id,d.treat_time,d.adddate,d.userid,d.treat_strength,d.treat_frequency,d.treat_waveform,d.treat_workmethod,d.treat_electrode,d.update_time,d.name,d.count    
from t_user u,treat_data d 

  		   and id = #{id} 
  		   and treat_time = #{treatTime} 
  		   and adddate = #{adddate} 
  		   and userid = #{userid} 
  		   and treat_strength = #{treatStrength} 
  		   and treat_frequency = #{treatFrequency} 
  		   and treat_waveform = #{treatWaveform} 
  		   and treat_workmethod = #{treatWorkmethod} 
  		   and treat_electrode = #{treatElectrode} 
  		   and update_time = #{updateTime} 
  		   and name = #{name} 
  		   and count = #{count} 


 	u.user_id = d.userid  and to_days(adddate) = to_days(now());

select u.name as name,d.id,d.treat_time,d.adddate,d.userid,d.treat_strength,d.treat_frequency,d.treat_waveform,d.treat_workmethod,d.treat_electrode,d.update_time,d.name,d.count    
from t_user u,treat_data d 

		   and d.id = #{id} 
  		   and d.treat_time = #{treatTime} 
  		   and d.adddate = #{adddate} 
  		   and d.userid = #{userid} 
  		   and d.treat_strength = #{treatStrength} 
  		   and d.treat_frequency = #{treatFrequency} 
  		   and d.treat_waveform = #{treatWaveform} 
  		   and d.treat_workmethod = #{treatWorkmethod} 
  		   and d.treat_electrode = #{treatElectrode} 
  		   and d.update_time = #{updateTime} 
  		   and d.name = #{name} 
  		   and d.count = #{count} 


	u.user_id = d.userid

select `id`,`treat_time`,`adddate`,`userid`,`treat_strength`,`treat_frequency`,`treat_waveform`,`treat_workmethod`,`treat_electrode`,`update_time`,`name`,`count` from treat_data

		   and id = #{id} 
  		   and treat_time = #{treatTime} 
  		   and adddate = #{adddate} 
  		   and userid = #{userid} 
  		   and treat_strength = #{treatStrength} 
  		   and treat_frequency = #{treatFrequency} 
  		   and treat_waveform = #{treatWaveform} 
  		   and treat_workmethod = #{treatWorkmethod} 
  		   and treat_electrode = #{treatElectrode} 
  		   and update_time = #{updateTime} 
  		   and name = #{name} 
  		   and count = #{count}

select count(*) from treat_data

  		   and id = #{id} 
